import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Settings, LogOut, Search, MessageCircle, Moon, Sun, Wifi, WifiOff } from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import { useTheme } from '../../context/ThemeContext';
import { api } from '../../services/api';
import { initSocket } from '../../services/socket';

const ChatList = () => {
  const [chats, setChats] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [onlineUsers, setOnlineUsers] = useState(new Set());
  const { user, logout } = useAuth();
  const { isDark, toggleTheme } = useTheme();
  const navigate = useNavigate();

  useEffect(() => {
    loadChats();
    setupSocket();
  }, [user]);

  const setupSocket = () => {
    if (!user) return;
    
    const socket = initSocket();
    
    // Connect with auth
    socket.auth = { token: user.id };
    socket.connect();
    
    // Listen for online/offline status
    socket.on('user_online', (data) => {
      setOnlineUsers(prev => new Set([...prev, data.user_id]));
    });
    
    socket.on('user_offline', (data) => {
      setOnlineUsers(prev => {
        const newSet = new Set(prev);
        newSet.delete(data.user_id);
        return newSet;
      });
    });

    return () => {
      socket.disconnect();
    };
  };

  const loadChats = async () => {
    if (!user) return;
    
    try {
      const response = await api.getChats(user.id);
      if (response.success) {
        setChats(response.data);
        // Extract online users
        const online = new Set(
          response.data
            .filter(chat => chat.online)
            .map(chat => chat.user_id)
        );
        setOnlineUsers(online);
      }
    } catch (error) {
      console.error('Failed to load chats:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredChats = chats.filter(chat => 
    chat.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
    chat.username.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const openChat = (chat) => {
    navigate(`/chat/${chat.id}`);
  };

  const getStatusColor = (chat) => {
    if (onlineUsers.has(chat.user_id) || chat.online) {
      return 'bg-green-500';
    }
    return 'bg-gray-400 dark:bg-gray-600';
  };

  const getStatusText = (chat) => {
    if (onlineUsers.has(chat.user_id) || chat.online) {
      return 'Online';
    }
    return chat.status || 'Offline';
  };

  if (loading) {
    return (
      <div className="h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading chats...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen bg-gray-50 dark:bg-gray-900 flex flex-col">
      {/* Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
              <User className="text-white text-lg" />
            </div>
            <div>
              <h1 className="text-xl font-semibold text-gray-800 dark:text-gray-100">Chats</h1>
              <p className="text-sm text-gray-600 dark:text-gray-400">Welcome back, {user?.name}</p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <button 
              onClick={toggleTheme}
              className="p-2 text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition"
            >
              {isDark ? <Sun size={20} /> : <Moon size={20} />}
            </button>
            <button 
              onClick={() => navigate('/profile')}
              className="p-2 text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition"
            >
              <Settings size={20} />
            </button>
            <button 
              onClick={logout}
              className="p-2 text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition"
            >
              <LogOut size={20} />
            </button>
          </div>
        </div>
      </div>

      {/* Search Bar */}
      <div className="px-6 py-4 bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search conversations..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
          />
        </div>
      </div>

      {/* Chat List */}
      <div className="flex-1 overflow-y-auto">
        {filteredChats.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-gray-500 dark:text-gray-400">
            <MessageCircle size={48} className="mb-4" />
            <p className="text-lg font-medium">No conversations yet</p>
            <p className="text-sm">Start a new chat to begin messaging</p>
          </div>
        ) : (
          <div className="divide-y divide-gray-200 dark:divide-gray-700">
            {filteredChats.map((chat) => (
              <div
                key={chat.id}
                onClick={() => openChat(chat)}
                className="px-6 py-4 hover:bg-gray-50 dark:hover:bg-gray-800 cursor-pointer transition"
              >
                <div className="flex items-center space-x-3">
                  <div className="relative">
                    <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
                      <span className="text-white font-semibold">
                        {chat.user.charAt(0).toUpperCase()}
                      </span>
                    </div>
                    <div className={`absolute -bottom-1 -right-1 w-4 h-4 ${getStatusColor(chat)} rounded-full border-2 border-white dark:border-gray-800`}></div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <h3 className="text-sm font-semibold text-gray-900 dark:text-gray-100 truncate">
                        {chat.user}
                      </h3>
                      <div className="flex items-center space-x-2">
                        {chat.unread_count > 0 && (
                          <span className="bg-blue-500 text-white text-xs rounded-full px-2 py-1 min-w-[20px] text-center">
                            {chat.unread_count}
                          </span>
                        )}
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {chat.timestamp || 'Now'}
                        </span>
                      </div>
                    </div>
                    <div className="flex items-center justify-between">
                      <p className="text-sm text-gray-600 dark:text-gray-400 truncate">
                        @{chat.username}
                      </p>
                      <div className="flex items-center space-x-1">
                        {onlineUsers.has(chat.user_id) || chat.online ? (
                          <Wifi size={12} className="text-green-500" />
                        ) : (
                          <WifiOff size={12} className="text-gray-400" />
                        )}
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {getStatusText(chat)}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatList;
