import React, { createContext, useState, useContext, useEffect } from 'react';
import { api } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    const token = localStorage.getItem('token');
    console.log('Checking auth status, token:', token); // Debug log
    
    if (token) {
      try {
        await loadUserProfile(token);
      } catch (error) {
        console.error('Failed to load profile:', error);
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        setUser(null);
      }
    }
    setLoading(false);
  };

  const loadUserProfile = async (token) => {
    try {
      console.log('Loading user profile for token:', token); // Debug log
      const response = await api.getProfile(token);
      console.log('Profile response:', response); // Debug log
      
      if (response.success) {
        setUser(response.data);
        setIsAuthenticated(true);
        console.log('User authenticated:', response.data); // Debug log
      } else {
        throw new Error('Failed to get profile');
      }
    } catch (error) {
      console.error('Profile load error:', error);
      localStorage.removeItem('token');
      setIsAuthenticated(false);
      setUser(null);
      throw error;
    }
  };

  const login = async (credentials) => {
    try {
      console.log('Attempting login with:', credentials.username); // Debug log
      const response = await api.login(credentials);
      console.log('Login response:', response); // Debug log
      
      if (response.success && response.token) {
        localStorage.setItem('token', response.token);
        await loadUserProfile(response.token);
        console.log('Login successful, user set:', user); // Debug log
        return response;
      } else {
        throw new Error(response.message || 'Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  };

  const signup = async (userData) => {
    try {
      console.log('Attempting signup with:', userData.username); // Debug log
      const response = await api.signup(userData);
      console.log('Signup response:', response); // Debug log
      return response;
    } catch (error) {
      console.error('Signup error:', error);
      throw error;
    }
  };

  const logout = () => {
    console.log('Logging out user'); // Debug log
    localStorage.removeItem('token');
    setUser(null);
    setIsAuthenticated(false);
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    login,
    signup,
    logout
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
