const BACKEND_URL = 'http://localhost:5000';

const handleResponse = async (res) => {
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.message || 'API error');
  }
  return res.json();
};

const api = {
  // ðŸ” Auth
  login: async (credentials) => {
    const res = await fetch(`${BACKEND_URL}/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(credentials),
    });
    return handleResponse(res);
  },

  signup: async (userData) => {
    const res = await fetch(`${BACKEND_URL}/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData),
    });
    return handleResponse(res);
  },

  // ðŸ‘¤ Profile
  getProfile: async (userId) => {
    const res = await fetch(`${BACKEND_URL}/user/${userId}`);
    return handleResponse(res);
  },

  updateProfile: async (userId, profileData) => {
    const res = await fetch(`${BACKEND_URL}/user/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(profileData),
    });
    return handleResponse(res);
  },

  // ðŸ’¬ Messages
  getMessages: async (chatId) => {
    const res = await fetch(`${BACKEND_URL}/messages/${chatId}`);
    return handleResponse(res);
  },

  sendMessage: async (messageData) => {
    const res = await fetch(`${BACKEND_URL}/messages`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(messageData),
    });
    return handleResponse(res);
  },

  // ðŸ“± Chats
  getChats: async (userId) => {
    const res = await fetch(`${BACKEND_URL}/chats/${userId}`);
    return handleResponse(res);
  },

  // ðŸ‘¥ Groups (if using group chat)
  getUserGroups: async (userId) => {
    const res = await fetch(`${BACKEND_URL}/groups/${userId}`);
    return handleResponse(res);
  },

  sendGroupMessage: async (messageData) => {
    const res = await fetch(`${BACKEND_URL}/group-messages`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(messageData),
    });
    return handleResponse(res);
  },

  getGroupMessages: async (groupId) => {
    const res = await fetch(`${BACKEND_URL}/group-messages/${groupId}`);
    return handleResponse(res);
  },
};

export default api;
